
-- data type tests
DROP TABLE IF EXISTS	model_draft.data_type_tests;
CREATE TABLE 		model_draft.data_type_tests (
	id 	serial,
	year 	integer,
	x_r	real,
	x_dp 	double precision,
	x_f	float, -- double precision
	x_n 	numeric,
	x_d 	decimal, -- numeric
	CONSTRAINT data_type_tests_pkey PRIMARY KEY (id) );

GRANT ALL ON TABLE model_draft.data_type_tests TO oeuser;

-- input
INSERT INTO model_draft.data_type_tests (year, x_r, x_dp, x_f, x_n, x_d) VALUES
	(2000,
	3.,
	3.,
	3.,
	3.,
	3.),
	(2001,
	1/3 ::real,
	1/3 ::double precision,
	1/3 ::float,
	1/3 ::numeric,
	1/3 ::decimal);

-- output
SELECT 	*
FROM	model_draft.data_type_tests;

WITH	mystring AS (
	SELECT	id	::text,
		year	::text,
		x_r	::text,
		x_dp	::text,
		x_f	::text,
		x_n	::text,
		x_d	::text
	FROM	model_draft.data_type_tests)
SELECT	length(id) AS id,
	length(year) AS year,
	length(x_r) AS x_r,
	length(x_dp) AS x_dp,
	length(x_f) AS x_f,
	length(x_n) AS x_n,
	length(x_d) AS x_d
FROM	mystring;
